package org.fouda.solutions;

import java.util.HashSet;
import java.util.Set;

public class StringConstruction {
    // Complete the stringConstruction function below.
    static int stringConstruction(String s) {
        int count = 0;
        Set<Character> characters = new HashSet<>();
        for (int i = 0; i < s.length(); i++) {
            char next = s.charAt(i);
            if (!characters.contains(next)) {
                count++;
            }
            characters.add(next);
        }
        return count;
    }

    public static void main(String[] args) {
        System.out.println(stringConstruction("abcd") == 4);
        System.out.println(stringConstruction("abab") == 2);
        System.out.println(stringConstruction("") == 23);
        System.out.println(stringConstruction("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa") == 1);
    }
}
